con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);

def word = ["жирафф", "карова", "копибара", "калибри"];
def answer = ["жираф", "корова", "капибара", "колибри"];

def testDataStep = testRunner.testCase.getTestStepByName('testData');
def request = context.testCase.getTestStepByName("checkText");

int i=0;
while ( i < 4 ) {  
    testDataStep.setPropertyValue('word',word[i]);
    log.info(" Sent word is " + "'" + word[i] + "'.");

    def run = testRunner.runTestStepByName("checkText");
    def response = run.getStatus().toString()

	def responseCurrentHolder = groovyUtils.getXmlHolder(request.name +"#Response");
	responseCurrentHolder.namespaces["sp"] = "http://speller.yandex.net/services/spellservice"
	def responseAnswer = responseCurrentHolder.getNodeValues("//sp:SpellResult[1]//sp:s").join("");

     if ( response == "OK" && answer[i] == responseAnswer ) {
		log.info(" Received answer " + "'" + responseAnswer + "'" + " matches with expected answer " + "'" + answer[i] + "'.");
		log.info(" - - - - - - - - - - - - - - - - - TEST_PASSED - - - - - - - - - - - - - - - - - ");
	} else {
    	     log.info(" Received answer " + "'" + responseAnswer + "'" + " doesn't match with expected answer " + "'" + answer[i] + "'.");
		log.info(" - - - - - - - - - - - - - - - - - TEST_FAILED - - - - - - - - - - - - - - - - - ");
    	}   
   i++;
 } 

log.info(" ");
log.info("                                                    ALL DONE");
